import React, { useState, useEffect, useRef } from 'react';
import { motion, useInView } from 'framer-motion';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './App.css';

// Register GSAP plugins
gsap.registerPlugin(ScrollTrigger);

const App = () => {
  const [isDark, setIsDark] = useState(true);

  const toggleDarkMode = () => {
    setIsDark(!isDark);
  };

  return (
    <div className={`${isDark ? 'dark' : ''}`}>
      <div className="bg-background-light dark:bg-background-dark font-sans text-slate-900 dark:text-gray-200 antialiased transition-colors duration-300 overflow-x-hidden">
        
        {/* Custom Cursor */}
        <CustomCursor />
        
        {/* Header */}
        <Header />
        
        {/* Main Content */}
        <main className="mx-auto px-6 w-full max-w-7xl">
          <HeroSection />
          <AboutSection />
          <SkillsSection />
          <PortfolioSection />
          <ContactSection />
        </main>
        
        {/* Footer */}
        <Footer />
        
        {/* Dark Mode Toggle */}
        <MagneticButton 
          className="right-6 bottom-6 z-50 fixed bg-primary shadow-lg p-4 rounded-full text-white hover:scale-110 transition-transform"
          onClick={toggleDarkMode}
        >
          <span className={`${isDark ? 'hidden' : 'block'} material-icons-round`}>dark_mode</span>
          <span className={`${isDark ? 'block' : 'hidden'} material-icons-round`}>light_mode</span>
        </MagneticButton>
      </div>
    </div>
  );
};

// Custom Cursor Component
const CustomCursor = () => {
  const cursorRef = useRef(null);

  useEffect(() => {
    const cursor = cursorRef.current;
    if (!cursor) return;

    const moveCursor = (e) => {
      gsap.to(cursor, {
        x: e.clientX,
        y: e.clientY,
        duration: 0.1,
        ease: "power2.out"
      });
    };

    const handleMouseEnter = () => {
      gsap.to(cursor, {
        scale: 1.5,
        duration: 0.3,
        ease: "power2.out"
      });
    };

    const handleMouseLeave = () => {
      gsap.to(cursor, {
        scale: 1,
        duration: 0.3,
        ease: "power2.out"
      });
    };

    // Add cursor movement
    document.addEventListener('mousemove', moveCursor);
    
    // Add hover effects for interactive elements
    const interactiveElements = document.querySelectorAll('button, a, input, textarea');
    interactiveElements.forEach(el => {
      el.addEventListener('mouseenter', handleMouseEnter);
      el.addEventListener('mouseleave', handleMouseLeave);
    });

    return () => {
      document.removeEventListener('mousemove', moveCursor);
      interactiveElements.forEach(el => {
        el.removeEventListener('mouseenter', handleMouseEnter);
        el.removeEventListener('mouseleave', handleMouseLeave);
      });
    };
  }, []);

  return (
    <div
      ref={cursorRef}
      className="custom-cursor fixed w-6 h-6 bg-primary rounded-full pointer-events-none z-[9999] mix-blend-difference"
      style={{ transform: 'translate(-50%, -50%)' }}
    />
  );
};

// Scroll Progress Bar Component
const ScrollProgress = () => {
  const { scrollYProgress } = useScroll();
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  return (
    <motion.div
      className="fixed top-0 left-0 right-0 h-1 bg-primary z-50 origin-left"
      style={{ scaleX }}
    />
  );
};

// Enhanced Magnetic Button Component with stronger effect for specific buttons
const MagneticButton = ({ children, className, onClick, isSpecial = false, ...props }) => {
  const buttonRef = useRef(null);

  useEffect(() => {
    const button = buttonRef.current;
    if (!button) return;

    const multiplier = isSpecial ? 0.6 : 0.4; // Stronger effect for special buttons

    const handleMouseMove = (e) => {
      const rect = button.getBoundingClientRect();
      const x = e.clientX - rect.left - rect.width / 2;
      const y = e.clientY - rect.top - rect.height / 2;
      
      gsap.to(button, {
        x: x * multiplier,
        y: y * multiplier,
        duration: 0.3,
        ease: "power2.out"
      });
    };

    const handleMouseLeave = () => {
      gsap.to(button, {
        x: 0,
        y: 0,
        duration: 0.5,
        ease: "elastic.out(1, 0.3)"
      });
    };

    button.addEventListener('mousemove', handleMouseMove);
    button.addEventListener('mouseleave', handleMouseLeave);

    return () => {
      button.removeEventListener('mousemove', handleMouseMove);
      button.removeEventListener('mouseleave', handleMouseLeave);
    };
  }, [isSpecial]);

  return (
    <button
      ref={buttonRef}
      className={`${className} cursor-pointer`}
      onClick={onClick}
      {...props}
    >
      {children}
    </button>
  );
};

// Floating Image Component
const FloatingImage = ({ children, className, intensity = 1 }) => {
  const imageRef = useRef(null);

  useEffect(() => {
    const image = imageRef.current;
    if (!image) return;

    // Create floating animation
    gsap.to(image, {
      y: -20 * intensity,
      duration: 3,
      ease: "power2.inOut",
      yoyo: true,
      repeat: -1
    });

    // Add subtle rotation
    gsap.to(image, {
      rotation: 2 * intensity,
      duration: 4,
      ease: "power2.inOut",
      yoyo: true,
      repeat: -1
    });

    return () => {
      gsap.killTweensOf(image);
    };
  }, [intensity]);

  return (
    <div ref={imageRef} className={className}>
      {children}
    </div>
  );
};

// Image Reveal Component using Framer Motion
const ImageReveal = ({ children, className, delay = 0 }) => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-50px" });

  return (
    <motion.div
      ref={ref}
      initial={{ scale: 0.8, opacity: 0 }}
      animate={isInView ? { scale: 1, opacity: 1 } : { scale: 0.8, opacity: 0 }}
      transition={{ 
        duration: 0.8, 
        delay: delay,
        ease: [0.25, 0.25, 0.25, 0.75]
      }}
      className={className}
    >
      {children}
    </motion.div>
  );
};

// Framer Motion Section Animation Variants
const sectionVariants = {
  hidden: { 
    opacity: 0, 
    y: 60 
  },
  visible: { 
    opacity: 1, 
    y: 0,
    transition: { 
      duration: 0.8, 
      ease: [0.25, 0.25, 0.25, 0.75]
    }
  }
};

const Header = () => {
  return (
    <motion.header 
      initial={{ y: -100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.8, ease: "easeOut" }}
      className="top-0 z-40 sticky bg-white/80 dark:bg-background-dark/80 backdrop-blur-md border-gray-100 dark:border-gray-800 border-b w-full"
    >
      <div className="flex justify-between items-center mx-auto px-6 max-w-7xl h-20">
        <div className="flex items-center gap-3">
          <div className="flex justify-center items-center bg-primary shadow-glow rounded-xl w-10 h-10 text-white">
            <span className="material-symbols-outlined">grid_view</span>
          </div>
          <span className="font-bold text-xl tracking-tight">Portfolio.</span>
        </div>
