const CustomCursor = () => {
  const cursorDotRef = useRef(null);
  const cursorRingRef = useRef(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const cursorDot = cursorDotRef.current;
    const cursorRing = cursorRingRef.current;
    
    if (!cursorDot || !cursorRing) return;

    // Check if device supports hover (desktop)
    const hasHover = window.matchMedia('(hover: hover)').matches;
    if (!hasHover) {
      setIsVisible(false);
      document.body.classList.remove('custom-cursor-active');
      return;
    }

    setIsVisible(true);
    document.body.classList.add('custom-cursor-active');

    let mouseX = 0;
    let mouseY = 0;

    const moveCursor = (e) => {
      mouseX = e.clientX;
      mouseY = e.clientY;
      
      // Move dot immediately
      gsap.to(cursorDot, {
        x: mouseX,
        y: mouseY,
        duration: 0,
        ease: "none"
      });
      
      // Move ring with lag for premium feel
      gsap.to(cursorRing, {
        x: mouseX,
        y: mouseY,
        duration: 0.15,
        ease: "power2.out"
      });
    };

    const handleMouseEnter = () => {
      // Scale up both elements when hovering interactive elements
      gsap.to(cursorDot, {
        scale: 2,
        backgroundColor: "#FF2E63",
        duration: 0.3,
        ease: "power2.out"
      });
      
      gsap.to(cursorRing, {
        scale: 1.5,
        borderColor: "#FF2E63",
        duration: 0.3,
        ease: "power2.out"
      });
    };

    const handleMouseLeave = () => {
      // Return to normal state
      gsap.to(cursorDot, {
        scale: 1,
        backgroundColor: "#FF2E63",
        duration: 0.3,
        ease: "power2.out"
      });
      
      gsap.to(cursorRing, {
        scale: 1,
        borderColor: "rgba(255, 46, 99, 0.3)",
        duration: 0.3,
        ease: "power2.out"
      });
    };

    const handleMouseDown = () => {
      gsap.to([cursorDot, cursorRing], {
        scale: 0.8,
        duration: 0.1,
        ease: "power2.out"
      });
    };

    const handleMouseUp = () => {
      gsap.to(cursorDot, {
        scale: 1,
        duration: 0.1,
        ease: "power2.out"
      });
      gsap.to(cursorRing, {
        scale: 1,
        duration: 0.1,
        ease: "power2.out"
      });
    };

    // Add event listeners
    document.addEventListener('mousemove', moveCursor);
    document.addEventListener('mousedown', handleMouseDown);
    document.addEventListener('mouseup', handleMouseUp);
    
    // Add hover effects for interactive elements
    const interactiveElements = document.querySelectorAll('button, a, input, textarea, [role="button"]');
    interactiveElements.forEach(el => {
      el.addEventListener('mouseenter', handleMouseEnter);
      el.addEventListener('mouseleave', handleMouseLeave);
    });

    return () => {
      document.removeEventListener('mousemove', moveCursor);
      document.removeEventListener('mousedown', handleMouseDown);
      document.removeEventListener('mouseup', handleMouseUp);
      document.body.classList.remove('custom-cursor-active');
      interactiveElements.forEach(el => {
        el.removeEventListener('mouseenter', handleMouseEnter);
        el.removeEventListener('mouseleave', handleMouseLeave);
      });
    };
  }, []);

  if (!isVisible) return null;

  return (
    <>
      {/* Cursor Dot */}
      <div
        ref={cursorDotRef}
        className="z-[9999] fixed bg-primary rounded-full w-2 h-2 cursor-dot pointer-events-none"
        style={{ transform: 'translate(-50%, -50%)' }}
      />
      
      {/* Cursor Ring */}
      <div
        ref={cursorRingRef}
        className="z-[9998] fixed border border-primary/30 rounded-full w-8 h-8 cursor-ring pointer-events-none"
        style={{ transform: 'translate(-50%, -50%)' }}
      />
    </>
  );
};

// Professional Custom Cursor Component
const MagneticButton = ({ children, className, onClick, isSpecial = false, ...props }) => {
  const buttonRef = useRef(null);

  useEffect(() => {
    const button = buttonRef.current;
    if (!button) return;

    const multiplier = isSpecial ? 0.6 : 0.4; // Stronger effect for special buttons

    const handleMouseMove = (e) => {
      const rect = button.getBoundingClientRect();
      const x = e.clientX - rect.left - rect.width / 2;
      const y = e.clientY - rect.top - rect.height / 2;
      
      gsap.to(button, {
        x: x * multiplier,
        y: y * multiplier,
        duration: 0.3,
        ease: "power2.out"
      });
    };

    const handleMouseLeave = () => {
      gsap.to(button, {
        x: 0,
        y: 0,
        duration: 0.5,
        ease: "elastic.out(1, 0.3)"
      });
    };

    button.addEventListener('mousemove', handleMouseMove);
    button.addEventListener('mouseleave', handleMouseLeave);

    return () => {
      button.removeEventListener('mousemove', handleMouseMove);
      button.removeEventListener('mouseleave', handleMouseLeave);
    };
  }, [isSpecial]);

  return (
    <button
      ref={buttonRef}
      className={`${className} cursor-pointer`}
      onClick={onClick}
      {...props}
    >
      {children}
    </button>
  );
};

// Floating Image Component
const FloatingImage = ({ children, className, intensity = 1 }) => {
  const imageRef = useRef(null);

  useEffect(() => {
    const image = imageRef.current;
    if (!image) return;

    // Create floating animation
    gsap.to(image, {
      y: -20 * intensity,
      duration: 3,
      ease: "power2.inOut",
      yoyo: true,
      repeat: -1
    });

    // Add subtle rotation
    gsap.to(image, {
      rotation: 2 * intensity,
      duration: 4,
      ease: "power2.inOut",
      yoyo: true,
      repeat: -1
    });

    return () => {
      gsap.killTweensOf(image);
    };
  }, [intensity]);

  return (
    <div ref={imageRef} className={className}>
      {children}
    </div>
  );
};

