                borderColor: 'rgba(255, 46, 99, 0.5)'
              }}
              transition={{ duration: 0.3 }}
              className="group flex flex-col bg-white/10 shadow-xl backdrop-blur-md border border-white/20 rounded-3xl h-full overflow-hidden cursor-pointer"
            >
              <div className={`relative flex justify-center items-center ${project.bgClass} p-8 h-64 overflow-hidden`}>
                <div className="absolute inset-0 bg-gradient-to-br from-white/5 to-transparent pointer-events-none"></div>
                
                {project.isSpecial ? (
                  <div className="z-10 relative flex justify-center items-center bg-white/20 shadow-2xl backdrop-blur-sm border border-white/30 rounded-3xl w-32 h-32 group-hover:scale-105 transition-transform duration-500">
                    <span className="bg-clip-text bg-gradient-to-br from-purple-400 to-red-400 font-black text-transparent text-6xl">b</span>
                  </div>
                ) : project.isBookHaven ? (
                  <div className="z-10 relative text-center">
                    <h3 className="mb-2 font-extrabold text-white text-3xl">BookHaven</h3>
                    <MagneticButton className="bg-primary/20 hover:bg-primary/30 shadow-lg backdrop-blur-sm px-6 py-2 border border-primary/30 rounded-full font-bold text-white text-xs transition-colors duration-300">
                      Explore Books
                    </MagneticButton>
                  </div>
                ) : (
                  <div className="flex items-center gap-3 scale-100 group-hover:scale-105 transition-transform duration-500">
                    <span className="font-bold text-white text-3xl tracking-tight">{project.title}</span>
                    <div className={`flex justify-center items-center ${project.iconBg} border-2 rounded-xl w-12 h-12`}>
                      <span className={`${project.iconColor} text-2xl material-icons-round`}>{project.icon}</span>
                    </div>
                  </div>
                )}
              </div>
              <div className="flex flex-col flex-1 p-6 md:p-8">
                <span className="block mb-3 font-bold text-primary text-xs uppercase tracking-widest">{project.category}</span>
                <div className="flex justify-between items-start mb-4">
                  <h2 className="font-bold text-white group-hover:text-primary text-2xl transition-colors">{project.title}</h2>
                  <button className="flex items-center gap-1 mt-1 font-semibold text-white/80 hover:text-primary text-sm transition-colors">
                    Live Link <span className="text-base material-icons-round">arrow_outward</span>
                  </button>
                </div>
                <p className="flex-1 mb-6 text-white/70 text-sm leading-relaxed">{project.description}</p>
              </div>
            </motion.article>
          </ImageReveal>
        ))}
      </div>
    </motion.section>
  );
};

const ContactSection = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-100px" });
  
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
  };

  const contactInfo = [
    {
      icon: 'email',
      label: 'Email',
      value: 'aputhecoder26@gmail.com'
    },
    {
      icon: 'call',
      label: 'Phone',
      value: '+880 1518671881'
    },
    {
      icon: 'location_on',
      label: 'Location',
      value: 'Chattogram, Bangladesh'
    }
  ];

  return (
    <motion.section 
      ref={ref}
      variants={sectionVariants}
      initial="hidden"
      animate={isInView ? "visible" : "hidden"}
      id="contact" 
      className="py-20 border-white/20 border-t"
    >
      <div className="mb-12 md:mb-16 text-center">
        <h3 className="mb-3 font-bold text-primary text-sm uppercase tracking-wider">Get In Touch</h3>
        <h1 className="font-bold text-white text-4xl md:text-5xl lg:text-6xl">
          Let's Work <span className="font-display font-semibold gradient-text-pink italic">Together</span>
        </h1>
      </div>

      <div className="gap-10 grid grid-cols-1 lg:grid-cols-2">
        <div className="space-y-6">
          {contactInfo.map((info, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.02, backgroundColor: 'rgba(255, 255, 255, 0.15)' }}
              transition={{ duration: 0.3 }}
              className="flex items-center gap-5 bg-white/10 shadow-sm backdrop-blur-md p-6 border border-white/20 rounded-2xl cursor-pointer"
            >
              <div className="flex justify-center items-center bg-primary/20 backdrop-blur-sm border border-primary/30 rounded-xl w-14 h-14 shrink-0">
                <span className="text-primary material-icons-round">{info.icon}</span>
              </div>
              <div className="overflow-hidden">
                <p className="mb-1 text-white/60 text-sm">{info.label}</p>
                <p className="font-bold text-white text-base sm:text-lg truncate">{info.value}</p>
              </div>
            </motion.div>
          ))}
        </div>

        <motion.form 
          whileHover={{ backgroundColor: 'rgba(255, 255, 255, 0.05)' }}
          transition={{ duration: 0.3 }}
          onSubmit={handleSubmit} 
          className="space-y-4 bg-white/10 backdrop-blur-md p-8 border border-white/20 rounded-2xl"
        >
          <input 
            type="text" 
            name="name"
            placeholder="Your Name" 
            value={formData.name}
            onChange={handleInputChange}
            className="bg-white/10 backdrop-blur-sm p-4 border border-white/20 focus:border-primary rounded-xl outline-none w-full text-white transition-colors placeholder-white/60"
          />
          
          <input 
            type="email" 
            name="email"
            placeholder="Your Email" 
            value={formData.email}
            onChange={handleInputChange}
            className="bg-white/10 backdrop-blur-sm p-4 border border-white/20 focus:border-primary rounded-xl outline-none w-full text-white transition-colors placeholder-white/60"
          />
          
          <textarea 
            name="message"
            placeholder="Your Message" 
            rows="4"
            value={formData.message}
            onChange={handleInputChange}
            className="bg-white/10 backdrop-blur-sm p-4 border border-white/20 focus:border-primary rounded-xl outline-none w-full text-white transition-colors resize-none placeholder-white/60"
          ></textarea>
          
          <MagneticButton 
            isSpecial={true}
            type="submit"
            className="flex justify-center items-center gap-2 bg-primary/20 hover:bg-primary/30 shadow-glow backdrop-blur-sm py-4 border border-primary/30 rounded-full w-full font-bold text-white transition-all duration-300"
          >
            Send Message <span className="text-sm material-symbols-outlined">send</span>
          </MagneticButton>
        </motion.form>
      </div>
    </motion.section>
  );
};

