import React, { useState, useEffect, useRef } from 'react';
import { motion, useInView, useAnimation } from 'framer-motion';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { ScrollToPlugin } from 'gsap/ScrollToPlugin';
import './App.css';

// Register GSAP plugins
gsap.registerPlugin(ScrollTrigger, ScrollToPlugin);

const App = () => {
  const [isDark, setIsDark] = useState(true);

  useEffect(() => {
    // Initialize smooth scrolling
    gsap.registerPlugin(ScrollTrigger);
    
    // Smooth scroll for anchor links
    const links = document.querySelectorAll('a[href^="#"]');
    links.forEach(link => {
      link.addEventListener('click', (e) => {
        e.preventDefault();
        const target = document.querySelector(link.getAttribute('href'));
        if (target) {
          gsap.to(window, {
            duration: 1.5,
            scrollTo: { y: target, offsetY: 80 },
            ease: "power2.inOut"
          });
        }
      });
    });

    return () => {
      ScrollTrigger.getAll().forEach(trigger => trigger.kill());
    };
  }, []);

  const toggleDarkMode = () => {
    setIsDark(!isDark);
  };

  return (
    <div className={`${isDark ? 'dark' : ''}`}>
      <div className="bg-background-light dark:bg-background-dark font-sans text-slate-900 dark:text-gray-200 antialiased transition-colors duration-300">
        
        {/* Header */}
        <Header />
        
        {/* Main Content */}
        <main className="mx-auto px-6 w-full max-w-7xl">
          <HeroSection />
          <AboutSection />
          <SkillsSection />
          <PortfolioSection />
          <ContactSection />
        </main>
        
        {/* Footer */}
        <Footer />
        
        {/* Dark Mode Toggle */}
        <MagneticButton 
          className="right-6 bottom-6 z-50 fixed bg-primary shadow-lg p-4 rounded-full text-white"
          onClick={toggleDarkMode}
        >
          <span className={`${isDark ? 'hidden' : 'block'} material-icons-round`}>dark_mode</span>
          <span className={`${isDark ? 'block' : 'hidden'} material-icons-round`}>light_mode</span>
        </MagneticButton>
      </div>
    </div>
  );
};

// Magnetic Button Component with GSAP
const MagneticButton = ({ children, className, onClick, ...props }) => {
  const buttonRef = useRef(null);

  useEffect(() => {
    const button = buttonRef.current;
    if (!button) return;

    const handleMouseMove = (e) => {
      const rect = button.getBoundingClientRect();
      const x = e.clientX - rect.left - rect.width / 2;
      const y = e.clientY - rect.top - rect.height / 2;
      
      gsap.to(button, {
        x: x * 0.3,
        y: y * 0.3,
        duration: 0.3,
        ease: "power2.out"
      });
    };

    const handleMouseLeave = () => {
      gsap.to(button, {
        x: 0,
        y: 0,
        duration: 0.5,
        ease: "elastic.out(1, 0.3)"
      });
    };

    button.addEventListener('mousemove', handleMouseMove);
    button.addEventListener('mouseleave', handleMouseLeave);

    return () => {
      button.removeEventListener('mousemove', handleMouseMove);
      button.removeEventListener('mouseleave', handleMouseLeave);
    };
  }, []);

  return (
    <button
      ref={buttonRef}
      className={`${className} hover:scale-110 transition-transform cursor-pointer`}
      onClick={onClick}
      {...props}
    >
      {children}
    </button>
  );
};

// Animated Section Wrapper
const AnimatedSection = ({ children, className = "", delay = 0 }) => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-100px" });
  const controls = useAnimation();

  useEffect(() => {
    if (isInView) {
      controls.start("visible");
    }
  }, [isInView, controls]);

  return (
    <motion.div
      ref={ref}
      animate={controls}
      initial="hidden"
      variants={{
        hidden: { opacity: 0, y: 50 },
        visible: { 
          opacity: 1, 
          y: 0,
          transition: { 
            duration: 0.8, 
            delay: delay,
            ease: [0.25, 0.25, 0.25, 0.75]
          }
        }
      }}
      className={className}
    >
      {children}
    </motion.div>
  );
};

const Header = () => {
  return (
    <motion.header 
      initial={{ y: -100, opacity: 0 }}
