      description: 'Creating full-stack applications using MongoDB, Express.js, React, and Node.js.'
    },
    {
      number: '03',
      title: 'Learning & Experimenting',
      description: 'Continuously improving my skills by experimenting with new frameworks.'
    }
  ];

  const techStack = ['React', 'JavaScript', 'Tailwind CSS', 'Node.js', 'MongoDB', 'Git'];

  useEffect(() => {
    // Animate skill bars with GSAP
    const skillBars = skillsRef.current?.querySelectorAll('.skill-bar');
    if (skillBars) {
      gsap.set(skillBars, { width: 0 });
      
      ScrollTrigger.create({
        trigger: skillsRef.current,
        start: "top 80%",
        onEnter: () => {
          skillBars.forEach((bar, index) => {
            const percentage = skills[index].percentage;
            gsap.to(bar, {
              width: `${percentage}%`,
              duration: 1.5,
              delay: index * 0.2,
              ease: "power2.out"
            });
          });
        }
      });
    }

    // Stagger animation for services
    const serviceCards = servicesRef.current?.querySelectorAll('.service-card');
    if (serviceCards) {
      gsap.set(serviceCards, { y: 50, opacity: 0 });
      
      ScrollTrigger.create({
        trigger: servicesRef.current,
        start: "top 80%",
        onEnter: () => {
          gsap.to(serviceCards, {
            y: 0,
            opacity: 1,
            duration: 0.8,
            stagger: 0.2,
            ease: "power2.out"
          });
        }
      });
    }
  }, []);

  return (
    <AnimatedSection>
      <section id="skills" className="py-20 border-gray-100 dark:border-gray-800 border-t">
