                  </div>
                </motion.div>
              ))}
            </div>
          </div>

          <div ref={servicesRef} id="services" className="space-y-6">
            <motion.h3 
              initial={{ x: 50, opacity: 0 }}
              whileInView={{ x: 0, opacity: 1 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
              className="mb-8 pl-4 border-secondary border-l-4 font-bold text-2xl"
