  const contactInfo = [
    {
      icon: 'email',
      label: 'Email',
      value: 'aputhecoder26@gmail.com'
    },
    {
      icon: 'call',
      label: 'Phone',
      value: '+880 1518671881'
    },
    {
      icon: 'location_on',
